mapper.update:
		
			UPDATE COMTNBBS SET
				NTT_ID = #{nttId, jdbcType=NUMERIC}
				, BBS_ID = #{bbsId}
				, NTT_NO = #{nttNo, jdbcType=NUMERIC}
				, NTT_SJ = #{nttSj}
				, NTT_CN = #{nttCn}
				, ANSWER_AT = #{answerAt}
				, PARNTSCTT_NO = #{parntscttNo, jdbcType=NUMERIC}
				, ANSWER_LC = #{answerLc, jdbcType=NUMERIC}
				, SORT_ORDR = #{sortOrdr, jdbcType=NUMERIC}
				, RDCNT = #{rdcnt, jdbcType=NUMERIC}
				, USE_AT = #{useAt}
				, NTCE_BGNDE = #{ntceBgnde}
				, NTCE_ENDDE = #{ntceEndde}
				, NTCR_ID = #{ntcrId}
				, NTCR_NM = #{ntcrNm}
				, PASSWORD = #{password}
				, ATCH_FILE_ID = #{atchFileId}
				, FRST_REGIST_PNTTM = #{frstRegistPnttm, jdbcType=DATE}
				, FRST_REGISTER_ID = #{frstRegisterId}
				, LAST_UPDT_PNTTM = #{lastUpdtPnttm, jdbcType=DATE}
				, LAST_UPDUSR_ID = #{lastUpdusrId}
		   WHERE BBS_ID = #{bbsId}
   AND NTT_ID = #{nttId}

mapper.delete:
		
		  DELETE FROM COMTNBBS 
		   WHERE BBS_ID = #{bbsId}
   AND NTT_ID = #{nttId}

mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( /* 구현 Sql */ SELECT NTT_ID , BBS_ID , NTT_NO , NTT_SJ , NTT_CN , ANSWER_AT , PARNTSCTT_NO , ANSWER_LC , SORT_ORDR , RDCNT , USE_AT , NTCE_BGNDE , NTCE_ENDDE , NTCR_ID , NTCR_NM , PASSWORD , ATCH_FILE_ID , FRST_REGIST_PNTTM , FRST_REGISTER_ID , LAST_UPDT_PNTTM , LAST_UPDUSR_ID FROM COMTNBBS WHERE 1=1 AND BBS_ID = #{bbsId}
AND NTT_SJ LIKE '%' || #{searchKeyword} || '%'
AND NTCR_ID LIKE '%' || #{searchKeyword} || '%'
AND NTCR_NM LIKE '%' || #{searchKeyword} || '%'
AND FRST_REGIST_PNTTM between #{searchStartDate} and #{searchEndDate}
FRST_REGIST_PNTTM FRST_REGIST_PNTTM LAST_UPDT_PNTTM
DESC ASC DESC
/* 구현 Sql */ ) ALL_LIST ) WHERE RNUM between #{firstIndex} and #{lastIndex}

SELECT COUNT(*) totcnt FROM COMTNBBS WHERE 1=1 AND BBS_ID = #{bbsId}
AND NTT_SJ LIKE '%' || #{searchKeyword} || '%'
AND NTCR_ID LIKE '%' || #{searchKeyword} || '%'
AND NTCR_NM LIKE '%' || #{searchKeyword} || '%'
AND FRST_REGIST_PNTTM between #{searchStartDate} and #{searchEndDate}
